phases = [phase_1, phase_2]

def move_butterfly():
    # function for moving
    doc["motyl"].xx += 5
    if doc["motyl"].xx > doc.getElementsByTagName("body")[0].width:
        doc["motyl"].xx = -200
    doc["motyl"].style.left = "{}px".format(doc["motyl"].xx)
    doc["motyl"].timer_move = timer.set_timeout(move_butterfly, doc["motyl"].time_move)

def fly_butterfly():
    # function for flying
    phase = doc["motyl"].phase
    phase = int(not phase)
    doc["motyl"].phase = phase
    doc["motyl"].innerHTML = phases[phase]
    doc["motyl"].timer_fly = timer.set_timeout(fly_butterfly, doc["motyl"].time_fly)

def stop():
    timer.clear_timeout(doc["motyl"].timer_move)
    timer.clear_timeout(doc["motyl"].timer_fly)

def stop_move():
    timer.clear_timeout(doc["motyl"].timer_move)

def stop_fly():
    timer.clear_timeout(doc["motyl"].timer_fly)


doc["motyl"].innerHTML = phases[doc["motyl"].phase]
doc["motyl"].style.width = "{}px".format(doc["butterfly1-a"].width)
move_butterfly()
fly_butterfly()
